    private void setInstituteRecycler(List<InstituteModel> instituteList) {
        RecyclerView.LayoutManager layoutManager = new LinearLayoutManager(this, RecyclerView.HORIZONTAL, false);
        instituteRecycler = findViewById(R.id.instituteRecycler);
        instituteRecycler.setLayoutManager(layoutManager);

        instituteAdapter = new InstituteAdapter(this, instituteList);
        instituteRecycler.setAdapter(instituteAdapter);
    }



